package com.demo;

public class IfConditionDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int age =55;	
		if(age>60){
		System.out.println("senior citizen");	
		}else if(age>50){
			System.out.println("middle age citizen");
		}else{
			System.out.println("young citizen");
		}
	}

}
